import { useState, useEffect } from "react";
import BlogList from './BlogList';

const Home = () => {

    const [blogs, setBlogs] = useState([
        {title: 'Iron Man', body: 'When Tony Stark, an industrialist, is captured, he constructs a high-tech armoured suit to escape. Once he manages to escape, he decides to use his suit to fight against evil forces to save the world.', author: 'Vivek Pareek', id: 1},
        {title: 'Man Of Steel', body: 'Clark learns about the source of his abilities and his real home when he enters a Kryptonian ship in the Artic. However, an old enemy follows him to Earth in search of a codex and brings destruction.', author: 'Vivek Pareek', id: 2},
        {title: 'La La Land', body: 'Sebastian (Ryan Gosling) and Mia (Emma Stone) are drawn together by their common desire to do what they love. But as success mounts they are faced with decisions that begin to fray the fragile fabric of their love affair, and the dreams they worked so hard to maintain in each other threaten to rip them apart.', author: 'Vivek Pareek', id: 3}
    ]);

    const handleDelete = (id) => {
        const newBlogs = blogs.filter(blog => blog.id !== id);
        setBlogs(newBlogs);
    };

    useEffect(() => {
        console.log('Effect ran');
    });

    return ( 
        <div className="home">
            <h2>Movies</h2>
            <BlogList blogs={blogs} handleDelete={handleDelete}/>
        </div>
     );
}

export default Home;
